/* eslint-disable jsx-a11y/heading-has-content */
import React from "react"

export default function ImagePopup (props) {
    const [card, setCard] = React.useState(null);
    React.useEffect(() => {
        if (props.card) {
            setCard(props.card)
        }
    })
    console.log(card)
    return (
    <div className={`popup popup_type_open-image  ${card && 'popup_active'}`}>
        <div className="popup__open-image">
            <button className="popup__close popup__close_type_card" type='button'></button>
            <img className="popup__main-image" alt='картинка' 
            // src={props.card && props.card.link}
            src={card.link}
            />
            <h2 className="popup__image-title"></h2>
        </div>
    </div>
    )
}
