import React from "react"
import PopupWithForm from "./PopupWithForm"

export default function ConfirmDeletePopup ({isOpen, onClose}) {
    const [confirm, setConfirm] = React.useState()
    function handleSubmit () {
        setConfirm(true)
    }
    return(
        <PopupWithForm
        formName="delete-form"
        name="confirm-delete"
        title="Вы уверены?"
        buttonText={'Да'}
        isOpen={isOpen}
        onClose={onClose}
        onSubmit={handleSubmit}
      />
    )
}
