import React from "react";
import PopupWithForm from "./PopupWithForm";

export default function EditProfilePopup (props) {
    const[name, setName] = React.useState('')
    const [description, setDescription] = React.useState('')

    return (
        <PopupWithForm
        name="user"
        formName="user-form"
        title="Редактировать профиль"
        buttonText={'Сохранить'}
        isOpen={this.props.isEditProfilePopupOpen}
        onClose={this.props.handleCloseClickOverlay}
      >
        <input
          id="_type_name"
          className="form__input form__input_type_name"
          name="name"
          type="text"
          minLength="2"
          maxLength="40"
          placeholder="Имя"
          required
          value={this.name}
          onChange={setName}
        />
        <span className="form__error form__error_type_name"></span>
        <input
          id="_type_info"
          className="form__input form__input_type_info"
          name="info"
          type="text"
          minLength="2"
          maxLength="200"
          placeholder="О себе"
          required
        />
        <span className="form__error form__error_type_info"></span>
      </PopupWithForm>
    )
}
