/* eslint-disable jsx-a11y/alt-text */
import React from 'react'
import { currentUserContext } from '../contexts/CurrentUserContext'
import { api } from '../utils/Api'
import Card from './Card'

export default class Main extends React.Component {
  static contextType = currentUserContext
  constructor(props) {
    super(props)
    this.handleEditAvatarClick = props.onEditAvatar
    this.handleEditPlaceClick = props.onAddPlace
    this.handleEditProfileClick = props.onEditProfile
    this.state = {
      cards: [],
    }
  }
  componentDidMount() {
    api
      .getInitialCards()
      .then((res) =>
        this.setState({
          cards: res,
        }),
      )
      .catch((err) => console.log(err))
  }
  componentDidUpdate() {
    this.handleCardLike = (card) => {
      const isLiked = card.likes.some((i) => i._id === this.context._id)
      if (isLiked) {
        api
          .deleteCardLike(card)
          // .then((newCard) => this.setState(this.state.cards.map(card => card._id === newCard._id? newCard: card))
          .then((newCard) => {
            this.setState(
              this.state.cards.map((card) =>
                card._id === newCard._id ? newCard : card,
              ),
            )
          })
          .catch((err) => console.log(err))
      } else {
        api
          .setCardLike(card)
          .then((newCard) => {
            this.setState(
              this.state.cards.map((card) =>
                card._id === newCard._id ? newCard : card,
              ),
            )
          })
          .catch((err) => console.log(err))
      }
    }
  }
  render() {
    console.log(1)
    return (
      <main className="main">
        <section className="user">
          <div className="user__profile">
            <img
              className="user__image"
              src={this.context.avatar}
              alt="Аватар"
            />
            <div
              className="user__image-overlay"
              onClick={this.handleEditAvatarClick}
            ></div>
            <h1 className="user__name">{this.context.name}</h1>
            <p className="user__info">{this.context.about}</p>
            <button
              className="user__change-button"
              type="button"
              onClick={this.handleEditProfileClick}
            ></button>
            <button
              className="user__add-button"
              type="button"
              onClick={this.handleEditPlaceClick}
            ></button>
          </div>
        </section>
        <section className="places">
          {this.state.cards.map((card) => (
            <Card
              card={card}
              key={card._id}
              onCardClick={this.props.onCardClick}
              onCardLike={this.handleCardLike}
            />
          ))}
        </section>
      </main>
    )
  }
}
